import React, { useState, useRef } from "react";
import "./App.css";

function App() {
  const [img, setImg] = useState("");
  const [typeOfEncrypt, setTypeOfEncrypt] = useState("");

  const styles = {
    hidden: {
      display: "none",
    },
    visible: {
      display: "block",
    },
  };

  const [chooseTypeStyleRead, setChooseTypeStyleRead] = useState(styles.hidden);
  const [chooseTypeStyleWrite, setChooseTypeStyleWrite] = useState(
    styles.hidden
  );
  const [writeData, setWriteData] = useState("");
  const inputFile = useRef(null);

  function radioButtonChanged(e) {
    if (e.target.value === "read") {
      setChooseTypeStyleRead(styles.visible);
      setChooseTypeStyleWrite(styles.hidden);
      setTypeOfEncrypt(0);
    } else {
      setChooseTypeStyleRead(styles.hidden);
      setChooseTypeStyleWrite(styles.visible);
      setTypeOfEncrypt(1);
    }
  }
  function imageEncrypt(e) {
    e.preventDefault();
    if (typeOfEncrypt !== "") encrypt();
  }
  function encrypt() {
    if (typeOfEncrypt) {
      console.log(true);
    } else if (inputFile.current) {
      const file = inputFile.current.files[0]
      console.log(file);
    }
  }
  return (
    <section className="wrapper">
      <h1>Laba 6 - image encrypt</h1>
      <header>
        <form>
          <label htmlFor="image">Choose img: </label>
          <input
            id="image"
            type="file"
            accept=".jpeg,.jpg"
            onChange={(e) => setImg(e.target.files[0].name)}
          ></input>
          <p>Choose type</p>
          <p>
            <label>
              Read from File{" "}
              <input
                type="radio"
                name="checkbox"
                value="read"
                onChange={radioButtonChanged}
              />
            </label>
            <label>
              Write Data{" "}
              <input
                type="radio"
                name="checkbox"
                value="write"
                onChange={radioButtonChanged}
              />
            </label>
          </p>
          <input
            type="file"
            accept=".txt"
            style={chooseTypeStyleRead}
            ref={inputFile}
          />
          <textarea
            style={chooseTypeStyleWrite}
            value={writeData}
            onChange={(e) => setWriteData(e.target.value)}
          ></textarea>
          <button onClick={imageEncrypt}>Submit</button>
        </form>
        <img src={img} alt="No img set"></img>
      </header>
      <textarea></textarea>
    </section>
  );
}

export default App;
