import React, { useState } from "react";
import "./App.css";

function App() {
  const [img, setImg] = useState("");

  const styles = {
    hidden: {
      display: "none",
    },
    visible: {
      display: "block",
    },
  };

  const [chooseTypeStyle, setChooseTypeStyle] = useState(styles.hidden);

  function imageLoad(e) {
    const file = e.target.files[0];
    setImg(file.name);
  }
  function radioButtonChanged(e) {
    if (e.target.value === "read") setChooseTypeStyle(styles.visible);
    else setChooseTypeStyle(styles.hidden);
  }
  return (
    <section className="wrapper">
      <h1>Laba 6 - image encrypt</h1>
      <header>
        <form>
          <label htmlFor="image">Choose img: </label>
          <input
            id="image"
            type="file"
            accept=".jpeg,.jpg"
            onChange={imageLoad}
          ></input>
          <p>Choose type</p>
          <p>
            <label>
              Read from File{" "}
              <input
                type="radio"
                name="checkbox"
                value="read"
                onChange={radioButtonChanged}
              />
            </label>
            <label>
              Write Data{" "}
              <input
                type="radio"
                name="checkbox"
                value="write"
                onChange={radioButtonChanged}
              />
            </label>
          </p>
          <input type="file" accept=".txt" style={chooseTypeStyle}></input>
        </form>
        <img src={img} alt="No img set"></img>
      </header>
      <textarea></textarea>
    </section>
  );
}

export default App;
