import React, { useState, useRef, useEffect } from "react";
import CryptoJS from "crypto-js";
import "./App.css";

function App() {
  const [img, setImg] = useState("");
  const [typeOfEncrypt, setTypeOfEncrypt] = useState("");

  const styles = {
    hidden: {
      display: "none",
    },
    visible: {
      display: "block",
    },
  };

  const [chooseTypeStyleRead, setChooseTypeStyleRead] = useState(styles.hidden);
  const [chooseTypeStyleWrite, setChooseTypeStyleWrite] = useState(
    styles.hidden
  );
  const [writeData, setWriteData] = useState("");
  const [resultData, setResultData] = useState("");
  const [degree, setDegree] = useState("");
  const inputFile = useRef(null);
  const inputImage = useRef(null);
  const canvasRef = useRef(null)
  const [text, setText] = useState("");

  function radioButtonChanged(e) {
    if (e.target.value === "read") {
      setChooseTypeStyleRead(styles.visible);
      setChooseTypeStyleWrite(styles.hidden);
      setTypeOfEncrypt(0);
    } else {
      setChooseTypeStyleRead(styles.hidden);
      setChooseTypeStyleWrite(styles.visible);
      setTypeOfEncrypt(1);
    }
  }
  function imageEncrypt(e) {
    e.preventDefault();
    if (typeOfEncrypt !== "" && degree !== "") encrypt();
  }
  function changeDegree(e) {
    if (
      e.target.value.length < 2 &&
      (e.target.value === "" || e.target.value > 0) &&
      e.target.value % 2 === 0
    )
      setDegree(e.target.value);
  }
  useEffect(() => {
    console.log(canvasRef.current)
    const canvas = canvasRef.current
    const content = canvas.getContent('2d')
    content.fillStyle = '#0000000'
    content.fillRect(0, 0, content.canvas.width, content.canvas.height)
  }, [canvasRef])
  useEffect(() => {
    if (text) {
      setResultData(`0) Изначальный текст\n${text}`);
      const key = CryptoJS.enc.Utf8.parse("aaaa");
      const iv = CryptoJS.enc.Utf8.parse("aaaa");
      let encrypted = CryptoJS.AES.encrypt(text, key, { iv: iv });
      setResultData((prev) => `${prev}\n\n1) AES\n${encrypted.toString()}`);
      const hash = CryptoJS.SHA256(encrypted);
      setResultData((prev) => `${prev}\n\n2) Hash via SHA2\n${hash}`);
      setResultData(
        (prev) => `${prev}\n\n3) Hash добавлен к файлу\n${hash}\n${text}`
      );
      encrypted = CryptoJS.AES.encrypt(`${hash}\n${text}`, key, {
        iv: iv,
      });
      setResultData((prev) => `${prev}\n\n4) AES\n${encrypted.toString()}`);
      secretImage();
      setResultData(
        (prev) =>
          `${prev}\n\n5) Поместить зашифрованный файл в BMP-контейнер по алгоритму LSB`
      );
      let decrypted = CryptoJS.AES.decrypt(encrypted.toString(), key, {
        iv: iv,
      }).toString(CryptoJS.enc.Utf8);
      setResultData((prev) => `${prev}\n\n7) Decrypted\n${decrypted}`);
      const message = decrypted.slice(hash.toString().length);
      const messageEncrypt = CryptoJS.AES.encrypt(message, key, { iv: iv });
      const hash2 = CryptoJS.SHA256(messageEncrypt);
      setResultData(
        (prev) =>
          `${prev}\n\n8) Целостность данных\nЦифровой ключ изначального текста равен: ${hash.toString()}
        \nЦифровой ключ полученного текста равен: ${hash2.toString()}
        \nПроверка на совпадение: ${hash.toString() === hash2.toString()}`
      );
    }
  }, [text]);
  function encrypt() {
    if (typeOfEncrypt) setText(writeData);
    else if (inputFile.current) {
      const file = inputFile.current.files[0];
      const reader = new FileReader();
      reader.readAsText(file);
      reader.onload = () => {
        setText(reader.result);
      };
    }
  }
  function secretImage() {
    const file = inputImage.current.files[0];
    const reader = new FileReader();
    const textByte = encodeURI(text).split(/%..|./).length - 1;
    reader.readAsArrayBuffer(file);

    reader.onload = () => {
      const iBuffer = reader.result;
      if (textByte >= (iBuffer.byteLength * degree) / 8 - 54) {
        alert("Ошибка. Длина текста превышает вместимость контейнера");
        return;
      }
      let u8 = new Uint8ClampedArray(iBuffer)
      console.log(u8)
    };
  }
  return (
    <section className="wrapper">
      <canvas ref={canvasRef}></canvas>
      <h1>Laba 6 - image encrypt</h1>
      <header>
        <form>
          <label htmlFor="image">Choose img: </label>
          <input
            id="image"
            type="file"
            accept=".jpeg,.jpg,.bmp"
            onChange={(e) => setImg(e.target.files[0].name)}
            ref={inputImage}
          ></input>
          <br />
          <label htmlFor="degree">Choose degree: </label>
          <input
            id="degree"
            type="text"
            value={degree}
            onChange={changeDegree}
          />
          <p>Choose type</p>
          <p>
            <label>
              Read from File{" "}
              <input
                type="radio"
                name="checkbox"
                value="read"
                onChange={radioButtonChanged}
              />
            </label>
            <label>
              Write Data{" "}
              <input
                type="radio"
                name="checkbox"
                value="write"
                onChange={radioButtonChanged}
              />
            </label>
          </p>
          <input
            type="file"
            accept=".txt"
            style={chooseTypeStyleRead}
            ref={inputFile}
          />
          <textarea
            style={chooseTypeStyleWrite}
            value={writeData}
            onChange={(e) => setWriteData(e.target.value)}
          ></textarea>
          <button onClick={imageEncrypt}>Submit</button>
        </form>
        <img src={img} alt="No img set"></img>
      </header>
      <textarea className="resultData" value={resultData} readOnly />
    </section>
  );
}

export default App;
